import { useState } from "react";
export default function App() {
  const [totalBill, setTotalBill] = useState(null);
  const [addBill1, setAddBill1] = useState(0);
  const [addBill2, setAddBill2] = useState(0);
  const [choice, setChoice] = useState(
    '<option value="0">Dissatisfied (0%)</option><option value="5">It was okay (5%)</option><option value="10">It was good (10%)</option><option value="20">Absolutely Amazing! (20%)</option>'
  );
  const additionalBillTotal = Math.round(
    totalBill * ((addBill1 + addBill2) / 200)
  );
  const endBill = Number(totalBill) + Number(additionalBillTotal);
  function handleReset() {
    setTotalBill(null);
  }
  return (
    <div>
      <Question QuestionText="How much was the bill?" />
      <input
        type="number"
        onChange={(e) => setTotalBill(e.target.value)}
        value={totalBill}
      ></input>
      <br />
      <QuestionAnswer
        onSelected={setAddBill1}
        QuestionText="How did you like the service?"
      >
        {choice}
      </QuestionAnswer>
      <QuestionAnswer
        onSelected={setAddBill2}
        QuestionText="How did your friend like the service?"
      >
        {choice}
      </QuestionAnswer>
      <h3>
        {totalBill !== null
          ? `You pay $${endBill} ($${totalBill} + $${additionalBillTotal} tip)`
          : ""}
      </h3>
      <button onClick={handleReset}>Reset</button>
    </div>
  );
}

function Question({ QuestionText }) {
  return <label>{QuestionText}</label>;
}
function QuestionAnswer({ onSelected, QuestionText, children }) {
  return (
    <>
      <label>{QuestionText}</label>
      <select onChange={(e) => onSelected(Number(e.target.value))}></select>
      <br />
    </>
  );
}
