import { useState } from "react";
export default function App() {
  const [totalBill, setTotalBill] = useState(null);
  function showBill() {
    setTotalBill(totalBill);
    {
      console.log(totalBill);
    }
  }
  return (
    <div>
      <Question QuestionText="How much was the bill?" />
      <input type="number" onChange={showBill} value={totalBill}>
        {totalBill}
      </input>
      <br />
      <QuestionAnswer>How did you like the service?</QuestionAnswer>
      <QuestionAnswer>How did your friend like the service?</QuestionAnswer>
      {totalBill !== null ? `You pay $ ($${totalBill} + $ tip)` : ""}
    </div>
  );
}

function Question({ QuestionText }) {
  return <label>{QuestionText}</label>;
}
function QuestionAnswer({ children }) {
  return (
    <>
      <label>{children}</label>
      <select>
        <option value="0">Dissatisfied (0%)</option>
        <option value="5">It was okay (5%)</option>
        <option value="10">It was good (10%)</option>
        <option value="20">Absolutely Amazing! (20%)</option>
      </select>
      <br />
    </>
  );
}
